select n.name, g.stable_id as GeneStableID, m.stable_id as TranscriptStableID, m.taxon_id as TaxonID, d.name as Chr, m.dnafrag_start as Chr_start, m.dnafrag_end as Chr_end, m.dnafrag_strand as Chr_strand from split_genes s join seq_member m using (seq_member_id) join gene_member g using(gene_member_id) join genome_db n on(m.genome_db_id=n.genome_db_id) join dnafrag d on (m.dnafrag_id=d.dnafrag_id) order by 1,5,6
